import { useState} from "react";
import { Row, Col } from "antd";
import { Select } from 'antd';
import AddToCart from "./AddToCart"

const { Option } = Select;


function ProductDetail({product}) {
    const [color, setColor] = useState();
   return (
      <Row gutter={[32, 32]}>
        <Col lg={{ span: 10, offset: 2 }}>
         <img
            alt=""
            className="product-image"
            src={product.image}
         />
               
        </Col>
        <Col lg={{span:12}}>
            <div >
                <h2 className="product-name">
                    {product.name}
                </h2>    
                <p className="product-author">
                    {product.author}
                </p>
                <p>$25.59</p>
                <p className="product-status">
                  Status: {product.countInStock > 0 ? "In Stock" : "Unavailable."}
               </p>
               <p className="product-color">
                    Color: {"   "}
                    <Select 
                        defaultValue={color} 
                        placeholder="Select color"
                        className="select-style"
                        onChange={val=>setColor(val)}
                    >
                        {[...Array(product.color.length).keys()].map((x) => (
                            <Option value={product.color[x]}>
                            {product.color[x]}
                            </Option>
                        ))}
                    </Select>
                </p>
                <div>
                <div className="purchasenumber">
                    <h3>购买数量</h3>
                    <div className="number">
                            <input className = "min" type="button">-</input>
                            <input type="number" min = "1" plus="1" value="1"></input>
                            <input className = "plus" type="button">+</input>
                    </div>
                </div>

                </div>
               
               <AddToCart />

            </div>

        </Col>
       
      </Row>
   );
}

export default ProductDetail;
