import { useEffect,useContext } from "react";
import Cookie from "js-cookie"
import { Button, notification } from "antd"
import { StoreContext } from "../store"
import { ADD_CART_ITEM } from "../utils/constants"
import { addCartItem } from "../action";
import { AliwangwangOutlined  } from '@ant-design/icons';

export default function AddToCart({ product, qty }) {
  const { dispatch } = useContext(StoreContext);
  const { state: { cartItems }} = useContext(StoreContext);

  

  const openNotification = () => {
    notification.open({
      message: `Your Cart ${product.img}` ,
      description:

      `You just add${qty} ${qty>1?"pieces":"Sea"} on Concrete Wall Clock 
      to your cart.`,
      icon:<AliwangwangOutlined  style={{ color: '#108ee9' }} />
     
    
      
    });
  }; 
      const addToCart = () => {
        openNotification();
        addCartItem(dispatch, product, qty);
      };

      useEffect(()=>{
        Cookie.set("cartItems", JSON.stringify(cartItems));
       }, [cartItems])

      return (
        <Button type="primary" className="btn-tocar" onClick={addToCart}>
          Add To Cart
        </Button>
      );
}
