export default function ProductItem( { product }) {
    return (

<section className="col-md-3 col-lg-3 pt-4 p-md-3 p-lg-4">
<div className="border1">
    <a href="/">
        <img className="w-100"
            src={product.image}
            alt="1" />
    </a>
    <div className="p-4">
        <h5 className=" mb-1">
            {product.name}
        </h5>
        <p >
            {product.author}
        </p>
    </div>
</div>
</section>
    );
}
