import { useState } from "react";
import { Row, Col } from "antd";
import { Select } from 'antd';
import products2 from "../json/products2.json";
//import AddToCart from "./AddToCart"

const { Option } = Select;
function ProductDetail({product}) {
    const [color, setColor] = useState();
   return (
      <Row gutter={[32, 32]}>
        <Col lg={{ span: 8, offset: 2 }}>
         <img
            src="https://i.pinimg.com/564x/03/3b/9a/033b9ad1772b989809582a87f25e6006.jpg"
         />           
        </Col>
        <Col lg={{span:12}}>
            <div class="product-name">
                <h2>Sea on Concrete Wall Clock</h2>
                <p>by cafelab</p>
                <p>$25.59</p>
               
               <p className="product-color">
                  Color: {"   "}
                  <Select 
                    defaultValue={color} 
                    placeholder="Select color"
                    className="select-style"
                    onChange={val=>setColor(val)}
                  >
                     {[...Array(products2.color).keys()].map((x) => (
                        <Option  value={products2.color[x]}>
                           {products2.color[x]}
                        </Option>
                     ))}
                  </Select>
               </p>

            </div>

        </Col>
       
      </Row>
   );
}

export default ProductDetail;
