import {Card}from "antd"
export default function ProductItem( { product }) {
    return (
<Card className="border1">
    <a href="/">
        <img 
            style={{width: '250px'}}
            src={product.image}
            alt="1" />
    </a>
    <div className="p-4">
        <h5 className=" mb-1">
            {product.name}
        </h5>
        <p >
            {product.author}
        </p>
    </div>

</Card>
    );
}
