import { useEffect,useContext } from "react";
import { Button, notification } from "antd"
import { StoreContext } from "../store"
import { ADD_CART_ITEM } from "../utils/constants"
import { addCartItem } from "../action";

export default function AddToCart({ product, qty }) {
  const { dispatch } = useContext(StoreContext);
  const { state: { cartItems }} = useContext(StoreContext);

  const openNotification = () => {
    notification.open({
      message: 'You just add this to your cart'
      ,
      description:

        "You just add" `${qty} ${qty>1?"pieces":"piece"} of ${product.author}' ${qty>1?"have":"has"} been added to your cart.`,
      onClick: () => {
        console.log('Notification Clicked!');
      },
      placement: 'Center'
      
    });
  }; 
      const addToCart = () => {
        openNotification();
        addCartItem(dispatch, product, qty);
      };

      useEffect(()=>{
        localStorage.setItem("cartItems", JSON.stringify(cartItems));
      }, [cartItems])

      return (
        <Button type="primary" className="btn-tocar" onClick={addToCart}>
          Add To Cart
        </Button>
      );
}
